    <div className='gameOver'>
        <h3>{gameOver.guessedWord ? "you correctly guessed" :"you failed"}</h3>
        <h1> Correct: {correctWord}</h1>
        (gameOver.guessedWord && (<h3> You guessed in {currAttempt.attempt} attempts</h3>)
    </div>
  )
}

export default GameOver
